import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

//There are two components in this one component file - Exercise and the Exercises list
// Exercise component is implemented as a functional react component - becuase it lacks state and lifecycle method
// If all we need to do is to accept props and return jsx - we need to use functional component
// This being a very small component we included it in this file itself
const Exercise = props => (
    <tr>
      <td>{props.exercise.username}</td>
      <td>{props.exercise.description}</td>
      <td>{props.exercise.duration}</td>
      <td>{props.exercise.date.substring(0,10)}</td> 
      {/* in the above line we are taking substring of the date, as that sub string only refers to date excluding time zone blah blah */}
      <td>
        <Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
      </td>
    </tr>
  )
// ExercisesList is a class component
export default class ExercisesList extends Component {
    constructor(props){
        super(props);

        this.deleteExercise = this.deleteExercise.bind(this);

        this.state = {exercises:[] };  
    }
    componentDidMount(){
        axios.get('http://localhost:5000/exercises/')
            .then(response => {
                this.setState({exercises: response.data})
            })
            .catch((error) =>{
                console.log(error);
            })
    }

    deleteExercise(id){
        axios.delete('http://localhost:5000/exercises/'+id)
            .then(res => console.log(res.data));
        this.setState({
            exercises: this.state.exercises.filter(el => el._id !== id) 
            //remove the exercise we are deleting from the exercises list -> React is very helpful in this case
            //_id is the mongoDB ID
        })
    }

    exerciseList() {
        //map is gonna returns a component Exercise for every element of the array
        return this.state.exercises.map(currentexercise => {
          return <Exercise exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
        })
      }


    render() {
        return(
            <div>
                <h3>Logged Exercises</h3>
                <table className="table">
                <thead className="thead-light">
                    <tr>
                    <th>Username</th>
                    <th>Description</th>
                    <th>Duration</th>
                    <th>Date</th>
                    <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    { this.exerciseList() }
                </tbody>
                </table>
            </div>
        )
    }
}
